mapper.sql:
and (asset.name LIKE CONCAT('%',replace(replace(replace(#{multipleQuery},'\\','\\\\'),'%','\%'),'_','\_'),'%') or asset.number LIKE CONCAT('%',replace(replace(replace(#{multipleQuery},'\\','\\\\'),'%','\%'),'_','\_'),'%') or air.ip like CONCAT('%',replace(replace(replace(#{multipleQuery},'\\','\\\\'),'%','\%'),'_','\_'),'%') or amr.mac like CONCAT('%',replace(replace(replace(#{multipleQuery},'\\','\\\\'),'%','\%'),'_','\_'),'%') )
and agr.asset_group_id in
#{item}
and asset.admittance_status =#{entryStatus}
and asset.admittance_status between 1 and 2
and asset.asset_status in
#{item}
and asset.status=1

mapper.update:
update asset
set admittance_status=#{updateStatus}
where id in

    #{item.stringId}

and status=1

mapper.select:
select asset.id stringId,asset.name,asset.number,group_concat(distinct air.ip) ip, group_concat(distinct amr.mac) mac,asset.admittance_status entryStatus, if(asset.asset_status =12 and asset.admittance_status = 2,false,true) isEntryOperation, asset_group as assetGroupName from asset left join asset_group_relation agr on asset.id=agr.asset_id and agr.status=1 left join asset_ip_relation air on asset.id=air.asset_id and air.status=1 left join asset_mac_relation amr on asset.id=amr.asset_id and amr.status=1 left join asset_entry_record aer on asset.id=aer.asset_id and aer.status=1
group by asset.id order by any_value(aer.gmt_create) desc,any_value(aer.id) desc
LIMIT #{pageSize} offset #{pageOffset}

select id assetId, admittance_status entryStatus from asset
and id in
#{item}
