SELECT ait.id as stringId, ait.id as templateId, ait.name as name, ait.number_code as numberCode, ait.gmt_modified as gmtModified, ait.description as description, ait.category_model as categoryModel, ait.current_status as currentStatus, ait.operation_system as operationSystem, ait.operation_system_name as operationSystemName, ait.gmt_create as gmtCreate, count(DISTINCT asit.software_id) as softNum, count(DISTINCT apit.patch_id) as patchNum FROM asset_install_template ait LEFT JOIN asset_software_install_template asit ON ait.id = asit.install_template_id AND asit.STATUS = 1 LEFT JOIN asset_patch_install_template apit ON ait.id = apit.install_template_id AND apit.STATUS = 1
and ait.status=1
and ait.`name` like concat('%',replace(replace(#{name},'%','\%'),'_','\_'),'%')
and ait.number_code like concat('%',replace(replace(#{numberCode},'%','\%'),'_','\_'),'%')
and ait.current_status = #{currentStatus }
and ait.operation_system = #{operationSystem }
and ait.operation_system_name = #{operationSystemName }
and ait.id=#{stringId}
GROUP BY ait.id order by ait.gmt_create desc
LIMIT #{pageSize} offset #{pageOffset}

