mapper.select:
select count(1) from ( SELECT DISTINCT ait.id AS stringId, ait.id AS templateId, ait.NAME AS NAME, ait.number_code AS numberCode, ait.gmt_modified AS gmtModified, ait.description AS description, count( DISTINCT asit.software_id ) AS softNum, count( DISTINCT apit.patch_id ) AS patchNum FROM asset_install_template ait LEFT JOIN asset_software_install_template asit ON ait.id = asit.install_template_id and asit.status=1 LEFT JOIN asset_patch_install_template apit ON ait.id = apit.install_template_id and apit.status=1
LEFT JOIN ( SELECT DISTINCT ahsl.business_id AS software_business_id FROM baseline_template bt LEFT JOIN baseline_template_software bts ON bt.id = bts.template_id AND bts.STATUS = 1 LEFT JOIN asset_hard_soft_lib ahsl ON bts.software_id = ahsl.id AND ahsl.STATUS = 1 WHERE bt.sw_type = 1 AND bt.id = #{baselineId}) t on t.software_business_id=asit.software_id where ait.status=1
and ait.`name` like concat('%',replace(replace(#{name},'%','\%'),'_','\_'),'%')
and ait.number_code like concat('%',replace(replace(#{numberCode},'%','\%'),'_','\_'),'%')
and ait.current_status = #{currentStatus }
and ait.operation_system = #{operationSystem }
and ait.operation_system_name = #{operationSystemName }
GROUP BY ait.id HAVING count(t.software_business_id)=0 ) t

SELECT DISTINCT ait.id AS stringId, ait.id AS templateId, ait.NAME AS NAME, ait.number_code AS numberCode, ait.gmt_modified AS gmtModified, ait.description AS description, count( DISTINCT asit.software_id ) AS softNum, count( DISTINCT apit.patch_id ) AS patchNum FROM asset_install_template ait LEFT JOIN asset_software_install_template asit ON ait.id = asit.install_template_id and asit.status=1 LEFT JOIN asset_patch_install_template apit ON ait.id = apit.install_template_id and apit.status=1
LEFT JOIN ( SELECT DISTINCT ahsl.business_id AS software_business_id FROM baseline_template bt LEFT JOIN baseline_template_software bts ON bt.id = bts.template_id AND bts.STATUS = 1 LEFT JOIN asset_hard_soft_lib ahsl ON bts.software_id = ahsl.id AND ahsl.STATUS = 1 WHERE bt.sw_type = 1 AND bt.id = #{baselineId}) t on t.software_business_id=asit.software_id where ait.status=1
and ait.`name` like concat('%',replace(replace(#{name},'%','\%'),'_','\_'),'%')
and ait.number_code like concat('%',replace(replace(#{numberCode},'%','\%'),'_','\_'),'%')
and ait.current_status = #{currentStatus }
and ait.operation_system = #{operationSystem }
and ait.operation_system_name = #{operationSystemName }
GROUP BY ait.id HAVING count(t.software_business_id)=0 order by ait.gmt_modified desc
LIMIT #{pageSize} offset #{pageOffset}

select count(1) from ( SELECT DISTINCT ait.id AS stringId, ait.id AS templateId, ait.NAME AS NAME, ait.number_code AS numberCode, ait.gmt_modified AS gmtModified, ait.description AS description,
count( DISTINCT asit.software_id ) AS softNum, count( DISTINCT apit.patch_id ) AS patchNum FROM asset_install_template ait LEFT JOIN asset_software_install_template asit ON ait.id = asit.install_template_id and asit.status=1 LEFT JOIN asset_patch_install_template apit ON ait.id = apit.install_template_id and apit.status=1 LEFT JOIN ( SELECT DISTINCT ahsl.business_id AS software_business_id FROM baseline_template bt LEFT JOIN baseline_template_software bts ON bt.id = bts.template_id AND bts.STATUS = 1 LEFT JOIN asset_hard_soft_lib ahsl ON bts.software_id = ahsl.id AND ahsl.STATUS = 1 WHERE bt.sw_type = 0 AND bt.id = #{baselineId}) t on t.software_business_id=asit.software_id where ait.status=1 and asit.software_id is not null
and ait.`name` like concat('%',replace(replace(#{name},'%','\%'),'_','\_'),'%')
and ait.number_code like concat('%',replace(replace(#{numberCode},'%','\%'),'_','\_'),'%')
and ait.current_status = #{currentStatus }
and ait.operation_system = #{operationSystem }
and ait.operation_system_name = #{operationSystemName }
GROUP BY ait.id HAVING count(t.software_business_id)=count(asit.software_id) ) t

SELECT DISTINCT ait.id AS stringId, ait.id AS templateId, ait.NAME AS NAME, ait.number_code AS numberCode, ait.gmt_modified AS gmtModified, ait.description AS description,
count( DISTINCT asit.software_id ) AS softNum, count( DISTINCT apit.patch_id ) AS patchNum FROM asset_install_template ait LEFT JOIN asset_software_install_template asit ON ait.id = asit.install_template_id and asit.status=1 LEFT JOIN asset_patch_install_template apit ON ait.id = apit.install_template_id and apit.status=1 LEFT JOIN ( SELECT DISTINCT ahsl.business_id AS software_business_id FROM baseline_template bt LEFT JOIN baseline_template_software bts ON bt.id = bts.template_id AND bts.STATUS = 1 LEFT JOIN asset_hard_soft_lib ahsl ON bts.software_id = ahsl.id AND ahsl.STATUS = 1 WHERE bt.sw_type = 0 AND bt.id = #{baselineId}) t on t.software_business_id=asit.software_id where ait.status=1 and asit.software_id is not null
and ait.`name` like concat('%',replace(replace(#{name},'%','\%'),'_','\_'),'%')
and ait.number_code like concat('%',replace(replace(#{numberCode},'%','\%'),'_','\_'),'%')
and ait.current_status = #{currentStatus }
and ait.operation_system = #{operationSystem }
and ait.operation_system_name = #{operationSystemName }
GROUP BY ait.id HAVING count(t.software_business_id)=count(asit.software_id) order by ait.gmt_modified desc
LIMIT #{pageSize} offset #{pageOffset}
