mapper.select:
select

from

where id = #{id}

SELECT a.id, a.asset_id as assetId, b.category_model as categoryModel, d.name as categoryModelName, a.asset_ip as assetIp, c.category_model as parentCategoryModel, e.name as parentCategoryModelName, a.asset_port as assetPort, a.parent_asset_id as parentAssetId, a.parent_asset_ip as parentAssetIp, a.parent_asset_port as parentAssetPort FROM asset_link_relation a LEFT JOIN asset b ON a.asset_id = b.id LEFT JOIN asset c ON a.parent_asset_id = c.id left join asset_category_model d on b.category_model = d.id left join asset_category_model e on c.category_model = e.id where 1 = 1
and a.asset_id = #{assetId}
and a.parent_asset_id = #{parentAssetId}
and b.category_model = #{categoryModel}
and (b.name like binary CONCAT('%',replace(replace(#{multipleQuery},'%','\%'),'_','\_'),'%') or b.number like binary CONCAT('%',replace(replace(#{multipleQuery},'%','\%'),'_','\_'),'%') or a.asset_ip like binary CONCAT('%',replace(replace(#{multipleQuery},'%','\%'),'_','\_'),'%') )
and (c.name like binary CONCAT('%',replace(replace(#{parentMultipleQuery},'%','\%'),'_','\_'),'%') or c.number like binary CONCAT('%',replace(replace(#{parentMultipleQuery},'%','\%'),'_','\_'),'%') or a.parent_asset_ip like binary CONCAT('%',replace(replace(#{parentMultipleQuery},'%','\%'),'_','\_'),'%') )

