mapper.select:
SELECT a.id as assetId, a.area_id as areaId, a.name, a.number, a.category_model, count( b.id ) AS canBind, count( b.id ) - count( c.id ) AS noBind, a.gmt_create FROM asset a LEFT JOIN asset_ip_relation b ON ( a.id = b.asset_id AND b.STATUS = 1 ) LEFT JOIN asset_link_relation c ON ( (a.id = c.asset_id AND b.ip = c.asset_ip) or (a.id = c.parent_asset_id AND b.ip = c.parent_asset_ip) AND c.STATUS = 1 ) WHERE b.asset_id IS NOT NULL
and a.area_id in
#{item}
and a.category_model= #{categoryModel}
and a.asset_status in
#{item}
and a.name like binary CONCAT('%',replace(replace(#{assetName},'%','\%'),'_','\_'),'%')
and a.number like binary CONCAT('%',replace(replace(#{assetNumber},'%','\%'),'_','\_'),'%')
GROUP BY a.id
order by ${sortName} ${sortOrder}, gmt_create desc
order by gmt_create desc
LIMIT #{pageSize} offset #{pageOffset}

select count(1) count from (SELECT a.id as assetId, a.area_id as areaId, a.name, a.number, a.category_model, count( b.id ) AS canBind, count( b.id ) - count( c.id ) AS noBind, a.gmt_create FROM asset a LEFT JOIN asset_ip_relation b ON ( a.id = b.asset_id AND b.STATUS = 1 ) LEFT JOIN asset_link_relation c ON ( (a.id = c.asset_id AND b.ip = c.asset_ip) or (a.id = c.parent_asset_id AND b.ip = c.parent_asset_ip) AND c.STATUS = 1 ) WHERE b.asset_id IS NOT NULL
and a.area_id in
#{item}
and a.category_model= #{categoryModel}
and a.asset_status in
#{item}
and a.name like binary CONCAT('%',replace(replace(#{assetName},'%','\%'),'_','\_'),'%')
and a.number like binary CONCAT('%',replace(replace(#{assetNumber},'%','\%'),'_','\_'),'%')
GROUP BY a.id) t

SELECT a.manufacturer FROM asset a LEFT JOIN asset_ip_relation b ON a.id = b.asset_id AND b. STATUS = 1 LEFT JOIN asset_link_relation d ON ( ( a.id = d.asset_id AND b.ip = d.asset_ip ) OR ( a.id = d.parent_asset_id AND b.ip = d.parent_asset_ip ) ) WHERE a. STATUS = 1
and a.id !=#{primaryKey}
GROUP BY a.id HAVING count(b.id) > count(d.id)

select a.id, a.area_id, a.asset_group, a.category_model, a.number, a.name, group_concat(b.ip) as ips, group_concat(c.mac) as macs from asset a left join asset_ip_relation b on a.id = b.asset_id and b.status = 1 left join asset_mac_relation c on a.id = c.asset_id and b.status = 1 left join asset_link_relation d on ((a.id = d.asset_id and b.ip = d.asset_ip ) or (a.id = d.parent_asset_id and b.ip = d.parent_asset_ip)) where a.status = 1
and a.id != #{primaryKey}
and a.category_model in
#{item}
group by a.id having count(b.id) > count(d.id) ORDER BY first_enter_nett desc,
FIELD(`id`,
#{item}
) DESC,
id desc
LIMIT #{pageSize} offset #{pageOffset}

select count(1) count from ( select a.id, a.area_id, a.asset_group, a.category_model, a.number, a.name, group_concat(b.ip) as ips, group_concat(c.mac) as macs from asset a left join asset_ip_relation b on a.id = b.asset_id and b.status = 1 left join asset_mac_relation c on a.id = c.asset_id and b.status = 1 left join asset_link_relation d on ((a.id = d.asset_id and b.ip = d.asset_ip ) or (a.id = d.parent_asset_id and b.ip = d.parent_asset_ip)) where a.status = 1
and a.id != #{primaryKey}
and a.category_model in
#{item}
group by a.id having count(b.id) > count(d.id) ) t

SELECT id,`NAME` FROM asset_group WHERE id IN ( SELECT asset_group_id FROM asset_group_relation grou where exists ( select * from asset_link_relation left join asset asset1 on asset_id = asset1.id LEFT JOIN asset asset2 on parent_asset_id = asset2.id where asset1.status=1 and asset2.status=1 and asset_link_relation.status=1
AND asset1.area_id in
#{areaId}
