SELECT a.id as stringId, a.`name`, a.`number`, a.manufacturer, a.asset_group as assetGroup, a.importance_degree as importanceDegree, a.category_model as categoryModel, GROUP_CONCAT(distinct air.ip) AS ips, GROUP_CONCAT(distinct amr.mac) AS macs, GROUP_CONCAT(distinct ab.name) AS businessName FROM asset AS a LEFT JOIN asset_ip_relation AS air ON air.asset_id = a.id and air.status=1 LEFT JOIN asset_mac_relation AS amr ON amr.asset_id = a.id and amr.status=1 LEFT JOIN asset_business_relation AS abr on a.id=abr.asset_id and abr.status=1 LEFT JOIN asset_business AS ab ON abr.unique_id=ab.unique_id and ab.status=1 where a.status=1
and a.id in
#{id}
group by a.id
LIMIT #{pageSize} offset #{pageOffset}

