SELECT asset.*, GROUP_CONCAT(distinct akm.key_num) as key_num, CONCAT_WS('/',asset.manufacturer,asset.name,asset.version) as mnv, GROUP_CONCAT( DISTINCT abr.asset_business_id ) AS asset_business, bt.tp_name as baseline_template_name, anm.net_type_name, acm.name as category_model_name, asset.gmt_create, GROUP_CONCAT(distinct air.ip) AS ips, GROUP_CONCAT(distinct amr.mac) AS macs FROM ( select asset.*, GROUP_CONCAT( DISTINCT air.ip ) AS ip, GROUP_CONCAT( DISTINCT amr.mac ) AS mac from asset left join asset_ip_relation air on (asset.id = air.asset_id and air.status = 1) left join asset_mac_relation amr on (asset.id = amr.asset_id and amr.status = 1)
group by asset.id ) asset left join asset_ip_relation air on (asset.id = air.asset_id and air.status = 1) left join asset_mac_relation amr on (asset.id = amr.asset_id and amr.status = 1) left join baseline_template_hist bt on asset.baseline_template_id = bt.id left join asset_business_relation abr on (asset.id=abr.asset_id and abr.status=1) left join asset_group_relation agr on (asset.id = agr.asset_id = agr.status = 1) left join asset_nettype_manage anm on (asset.net_type = anm.id and anm.status = 1) left join asset_category_model acm on (asset.category_model = acm.id and acm.status = 1) left join asset_key_manage akm on (akm.key_user_type = 1 and asset.id = akm.key_user_id and akm.is_delete = 1)
left join asset_user au on (asset.responsible_user_id = au.id and au.status = 1)
GROUP BY asset.id ORDER BY
${sortName} ${sortOrder}
gmt_create ${sortOrder}
asset.id desc
LIMIT #{pageSize} offset #{pageOffset}
limit #{start},#{end}

